import React from "react";
import ReactDOM from "react-dom";
import "./index.css";
const name="Hilal";
const lname="Ahmad";
//another challenge 
const date=new Date().toLocaleDateString();  
const curTime=new Date().toLocaleTimeString(); 
const pic="https://picsum.photos/200/300"; 
const pic1="https://picsum.photos/300/300"; 
const pic2="https://picsum.photos/250/300/?blur=2"; 

const link="https://atmosbit.com"; 
const heading={
  color:"green",
  textAlign:"center"

};
const shadows={
  padding:"50px",
  boxShadow: "rgba(17, 12, 46, 0.15) 0px 48px 100px 0px"
}; 
const cssStyle={};
 //#3 challange  
 const grt="";
 const today_time= new Date();
 const h=today_time.getHours();
 const greating=((grt)=>{
  if(h>=1 && h <=11){
    grt="Good Morning";
    cssStyle.color="green";
    cssStyle.textAlign=" center";

  }
  else if(h>=12 && h<=18){

    grt="Good AfterNoon";
    cssStyle.color="Yellow";
    // cssStyle.border=" 3px solid green";
    cssStyle.textAlign=" center";
  }
  else{
    grt="Good Night";
    cssStyle.color="black";
    cssStyle.textAlign=" center";

  }
  return grt;
 });

ReactDOM.render(
  // this is when you do it with simple dive
  // <div>
  //   <h1>hello world</h1>
  // <p>lorem this new paragraph  </p>
  // </div>,
  // do it with array
  // [
  //   <h1>hello world</h1>,
  //   <p>lorem this new paragraph  </p>,
  // ],

  // now we are writing React Fragment
<>
<p>2 challenge is to show date </p>
<p>{`Date: ${date}`}</p>
<p>{`Date: ${curTime}`}</p>
{/* <p>{`Date: ${h}`}</p> */}


<h1>hello this is React Fragmentation</h1>
<p>this is testing of React Fragmentation</p>
<>
Netfilx series
<ol>
  <li>orange</li>
  <li>car racing</li>
  <li>mountain</li>
  <li>swimming</li>
</ol>
</>
<h1>how to use expression in jsx</h1>
<>
<h1>My name is {name}</h1>
<p>{`now we are trying templete literal with my name ${name} ${lname}`}</p>
{/* //this is good morning challange */}
<h1 style={textAlign}>Hello Sir, <span style={cssStyle}>{greating()}</span></h1>
{/* image gallery  */}
<div className="div">
<img src={pic} alt="new picture from lorem picsum"/>
<a href={link}>
<img src={pic1} alt="mountain picture from lorem picsum" className="ctpic"/></a>
<img src={pic2} alt="random picture from lorem picsum"/>
</div>
{/* image gallery  */}

</>
<>
<h1>NOw we are learning inline css</h1>
<p style={{fontSize:"30px",textAlign:'center'}}>This paragraph we are learning about inline css </p>
<p style={heading}>This is internal css effect</p>
<div style={shadows}>
  <p style={{fontSize:"30px",textAlign:'center'}}>this is new paragraph with internal css shadows</p>
</div>
</>
</>,

  document.getElementById("root")
