import React from 'react';
const Paragraph=(()=>{
    return <p> React.js, a popular JavaScript library for building user interfaces, revolves around the concept of components. Components are modular, reusable building blocks that encapsulate specific functionality and UI elements. They allow developers to create complex user interfaces by composing smaller, manageable parts.</p>;
});

export default Paragraph;
